refentry.refsect1:
&reftitle.parameters;




   binary_string



     The binary string to convert

&reftitle.changelog;







     &Version;
     &Description;





     Since 4.1.0

      The function can now convert numbers that are too large to
      fit into the platforms integer type, larger values
      are returned as float in that case.

  &reftitle.examples;



    bindec example


<?php
echo bindec('110011') . "\n";
echo bindec('000110011') . "\n";

echo bindec('111');
?>


    &example.outputs;


51
51
7







    bindec interprets input as unsigned integers


<?php
/*
 * The lesson from this example is in the output
 * rather than the PHP code itself.
 */

$magnitude_lower = pow(2, (PHP_INT_SIZE * 8) - 2);
p($magnitude_lower - 1);
p($magnitude_lower, 'see the rollover?');

p(PHP_INT_MAX, 'PHP_INT_MAX');
p(~PHP_INT_MAX, 'interpreted to be one more than PHP_INT_MAX');
p(-1, 'interpreted to be the largest unsigned integer');

function p($input, $note = '') {
    $format = '%0' . (PHP_INT_SIZE * 8) . 'b';

    echo "input:        $input\n";
    $bin = sprintf($format, $input);
    echo "binary:       $bin\n";

    $dec = bindec($bin);
    echo 'bindec():     ' . $dec . "\n";

    if (PHP_INT_SIZE == 8) {
        echo 'dec as float: ' . sprintf('%f', $dec) . "\n";
        echo "64 bit note:  float lacks precision in this range\n";
    }

    if ($note) {
        echo "NOTE:         $note\n";
    }

    echo "\n";
}
?>


