refentry.refsect1:
  &reftitle.examples;



    A Phar->isFlushingToPhar example





<?php
$p = new Phar(dirname(__FILE__) . '/brandnewphar.phar', 0, 'brandnewphar.phar');
$p['file1.txt'] = 'hi';
var_dump($p->isFlushingToPhar());
$p->begin();
var_dump($p->isFlushingToPhar());
$p->commit();
var_dump($p->isFlushingToPhar());
?>


    &example.outputs;


bool(true)
bool(false)
bool(true)
